import React, { useState } from 'react';
import { Box, Button, Typography, Card, CardContent, TextField, Snackbar, Alert, Stack, InputAdornment, IconButton } from '@mui/material';
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import EmailIcon from '@mui/icons-material/Email';
import LockIcon from '@mui/icons-material/Lock';
import Visibility from '@mui/icons-material/Visibility';
import VisibilityOff from '@mui/icons-material/VisibilityOff';

export default function Login() {
  const history = useHistory();
  const [form, setForm] = useState({ email: '', senha: '' });
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'info' });
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setLoading(true);
    try {
      const res = await axios.post('http://192.168.0.27:5000/api/auth/login', form);
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      setSnackbar({ open: true, message: 'Login realizado com sucesso!', severity: 'success' });
      setTimeout(() => history.push('/app'), 1000);
    } catch (err) {
      setSnackbar({ open: true, message: err.response?.data?.error || 'Erro ao fazer login.', severity: 'error' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box 
      sx={{ 
        minHeight: '100vh',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'linear-gradient(135deg, #E0E0DF 0%, #023C4F 100%)',
        position: 'relative',
        overflow: 'hidden',
        p: { xs: 2, sm: 4 }
      }}
    >
      {/* Partículas animadas */}
      <Box
        sx={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          zIndex: 1,
          opacity: 0.15,
          '&::before': {
            content: '""',
            position: 'absolute',
            top: '50%',
            left: '50%',
            width: '600px',
            height: '600px',
            background: 'radial-gradient(circle, #FBD43722 0%, transparent 70%)',
            transform: 'translate(-50%, -50%)',
            animation: 'pulse 4s ease-in-out infinite'
          },
          '&::after': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'url("data:image/svg+xml,%3Csvg width=\'100\' height=\'100\' viewBox=\'0 0 100 100\' xmlns=\'http://www.w3.org/2000/svg\'%3E%3Ccircle cx=\'50\' cy=\'50\' r=\'1.5\' fill=\'%23023C4F\' fill-opacity=\'0.08\'/%3E%3C/svg%3E")',
            backgroundSize: '100px 100px',
            animation: 'float 8s ease-in-out infinite'
          }
        }}
      />

      <Card 
        sx={{ 
          maxWidth: 400,
          width: '100%',
          background: '#E0E0DF',
          borderRadius: 4,
          border: '2px solid #FBD437',
          boxShadow: '0 8px 32px 0 rgba(2,60,79,0.10)',
          position: 'relative',
          zIndex: 2,
          overflow: 'hidden',
          '&::before': {
            content: '""',
            position: 'absolute',
            top: 0,
            left: '-100%',
            width: '200%',
            height: '4px',
            background: 'linear-gradient(90deg, transparent, #FBD43788, transparent)',
            animation: 'shimmer 4s infinite linear'
          }
        }}
      >
        <CardContent sx={{ p: 4 }}>
          <Stack spacing={3}>
            <Box sx={{ textAlign: 'center', mb: 3 }}>
              {/* Espaço para a Logo */}
              <Box 
                sx={{ 
                  width: '120px',
                  height: '120px',
                  margin: '0 auto 24px',
                  background: 'linear-gradient(135deg, #FBD43722 0%, #023C4F11 100%)',
                  borderRadius: '24px',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  border: '2px solid #FBD437',
                  boxShadow: '0 8px 32px 0 rgba(2,60,79,0.08)',
                  position: 'relative',
                  '&::before': {
                    content: '""',
                    position: 'absolute',
                    inset: '-1px',
                    borderRadius: '24px',
                    padding: '1px',
                    background: 'linear-gradient(135deg, #FBD43744 0%, #023C4F22 100%)',
                    WebkitMask: 'linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0)',
                    WebkitMaskComposite: 'xor',
                    maskComposite: 'exclude',
                  }
                }}
              >
                {/* Aqui você pode adicionar sua logo como imagem */}
                <Typography 
                  variant="h4" 
                  sx={{ 
                    color: '#FBD437',
                    fontWeight: 800,
                    fontSize: '2rem'
                  }}
                >
                  AS
                </Typography>
              </Box>

              <Typography 
                variant="h4" 
                fontWeight={800} 
                sx={{ 
                  mb: 1,
                  color: '#023C4F'
                }}
              >
                Bem-vindo de volta!
              </Typography>
              <Typography 
                color="#023C4F"
                sx={{ fontSize: 16 }}
              >
                Entre com suas credenciais para continuar
              </Typography>
            </Box>

            <Stack spacing={3} component="form" onSubmit={handleSubmit}>
              <TextField
                fullWidth
                label="Email"
                name="email"
                type="email"
                value={form.email}
                onChange={handleChange}
                required
                variant="outlined"
                InputProps={{
                  style: { background: '#fff', color: '#023C4F', borderRadius: 12 },
                  startAdornment: (
                    <InputAdornment position="start">
                      <EmailIcon sx={{ color: '#FBD437' }} />
                    </InputAdornment>
                  )
                }}
                InputLabelProps={{
                  sx: { 
                    color: '#94A3B8',
                    '&.Mui-focused': {
                      color: 'rgba(59, 130, 246, 0.8)'
                    }
                  }
                }}
              />

              <TextField
                fullWidth
                label="Senha"
                name="senha"
                type={showPassword ? 'text' : 'password'}
                value={form.senha}
                onChange={handleChange}
                required
                variant="outlined"
                InputProps={{
                  style: { background: '#fff', color: '#023C4F', borderRadius: 12 },
                  startAdornment: (
                    <InputAdornment position="start">
                      <LockIcon sx={{ color: '#FBD437' }} />
                    </InputAdornment>
                  ),
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton onClick={() => setShowPassword(s => !s)} edge="end" tabIndex={-1}>
                        {showPassword ? <VisibilityOff sx={{ color: '#023C4F' }} /> : <Visibility sx={{ color: '#FBD437' }} />}
                      </IconButton>
                    </InputAdornment>
                  )
                }}
                InputLabelProps={{
                  sx: { 
                    color: '#94A3B8',
                    '&.Mui-focused': {
                      color: 'rgba(59, 130, 246, 0.8)'
                    }
                  }
                }}
              />

              <Button
                type="submit"
                fullWidth
                size="large"
                variant="contained"
                color="primary"
                disabled={loading}
                sx={{ mt: 1, fontWeight: 700, fontSize: 18, borderRadius: 12, boxShadow: '0 2px 8px 0 #FBD43733', background: '#FBD437', color: '#023C4F', '&:hover': { background: '#ffe066', color: '#023C4F' } }}
              >
                Entrar
              </Button>

              <Typography sx={{ textAlign: 'center', color: '#023C4F', mt: 2 }}>
                Não tem uma conta?{' '}
                <Button color="secondary" sx={{ fontWeight: 700, textTransform: 'none', color: '#FBD437' }} onClick={() => history.push('/cadastro-paciente')}>
                  Cadastre-se
                </Button>
              </Typography>
            </Stack>
          </Stack>
        </CardContent>
      </Card>

      <Snackbar 
        open={snackbar.open} 
        autoHideDuration={2500} 
        onClose={() => setSnackbar({ ...snackbar, open: false })}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert 
          severity={snackbar.severity}
          variant="filled"
          sx={{ 
            borderRadius: 3,
            bgcolor: snackbar.severity === 'success' ? 'rgb(21, 128, 61)' : 'rgb(180, 35, 24)'
          }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
}
