import React from 'react';
import { Box, Button, Typography, Card, CardContent } from '@mui/material';
import LocalHospitalIcon from '@mui/icons-material/LocalHospital';
import PersonIcon from '@mui/icons-material/Person';
import { useHistory } from 'react-router-dom';

export default function AuthHome() {
  const history = useHistory();
  return (
    <Box sx={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', 
      background: 'radial-gradient(circle at 50% 30%, #23272f 60%, #181a20 100%)' }}>
      <Card sx={{ p: { xs: 3, sm: 5 }, borderRadius: 6, boxShadow: '0 8px 32px 0 #0006', minWidth: 320, maxWidth: 380, background: 'rgba(30,34,44,0.96)', backdropFilter: 'blur(10px)', transition: 'box-shadow 0.2s' }}>
        <CardContent>
          <Typography variant="h4" fontWeight={800} color="#fff" align="center" mb={2} sx={{ letterSpacing: 1 }}>
            Bem-vindo ao App Sacados
          </Typography>
          <Typography color="#b0b8c1" align="center" mb={4} sx={{ fontSize: 17 }}>
            Escolha seu perfil para continuar:
          </Typography>
          <Box display="flex" flexDirection="column" gap={3}>
            <Button
              variant="contained"
              size="large"
              startIcon={<LocalHospitalIcon sx={{ fontSize: 30 }} />}
              sx={{
                borderRadius: 4,
                fontWeight: 800,
                fontSize: 20,
                py: 2,
                background: 'linear-gradient(90deg, #1976d2 60%, #0d47a1 100%)',
                boxShadow: '0 2px 16px 0 #1976d2aa',
                letterSpacing: 1,
                transition: 'all 0.2s',
                '&:hover': {
                  background: 'linear-gradient(90deg, #2196f3 60%, #1976d2 100%)',
                  boxShadow: '0 4px 32px 0 #2196f3cc',
                  transform: 'scale(1.03)'
                }
              }}
              onClick={() => history.push('/cadastro-clinica')}
            >
              Sou Clínica
            </Button>
            <Button
              variant="outlined"
              size="large"
              startIcon={<PersonIcon sx={{ fontSize: 28 }} />}
              sx={{
                borderRadius: 4,
                fontWeight: 800,
                fontSize: 20,
                py: 2,
                color: '#fff',
                borderColor: '#1976d2',
                letterSpacing: 1,
                transition: 'all 0.2s',
                '&:hover': {
                  borderColor: '#2196f3',
                  color: '#2196f3',
                  background: 'rgba(25, 118, 210, 0.08)',
                  transform: 'scale(1.03)'
                }
              }}
              onClick={() => history.push('/cadastro-paciente')}
            >
              Sou Paciente
            </Button>
            <Button
              variant="text"
              sx={{ color: '#b0b8c1', mt: 2, fontWeight: 500, fontSize: 15, letterSpacing: 1, '&:hover': { color: '#1976d2', textDecoration: 'underline' } }}
              onClick={() => history.push('/login')}
            >
              Já tenho uma conta
            </Button>
          </Box>
        </CardContent>
      </Card>
    </Box>
  );
}
