import React, { useState } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  Box,
  Typography,
  Avatar,
  IconButton,
  Stack
} from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import SendIcon from '@mui/icons-material/Send';
import axios from 'axios';

export default function ChatModal({ open, onClose, clinica }) {
  const user = JSON.parse(localStorage.getItem('user'));
  const [enviado, setEnviado] = useState(false);

  React.useEffect(() => {
    if (open && clinica) {
      const mensagemPadrao = `Olá, meu nome é ${user?.nome || 'Paciente'}. Gostaria de agendar uma consulta para ${clinica?.setor || clinica?.especialidade || ''}${clinica?.tratamento ? ' - ' + clinica.tratamento : ''}. Por favor, me informe os horários disponíveis.`;
      axios.post('http://192.168.0.27:5000/api/mensagens', {
        clinicaId: clinica._id || clinica.id,
        remetente: 'paciente',
        texto: mensagemPadrao
      }).then(() => setEnviado(true));
    }
  }, [open, clinica]);

  return (
    <Dialog
      open={open}
      onClose={() => { setEnviado(false); onClose(); }}
      maxWidth="sm"
      fullWidth
      PaperProps={{
        sx: {
          background: 'rgba(17, 25, 40, 0.95)',
          backdropFilter: 'blur(20px)',
          borderRadius: 2,
          border: '1px solid rgba(255,255,255,0.1)',
          boxShadow: '0 8px 32px 0 rgba(0,0,0,0.4)'
        }
      }}
    >
      <DialogTitle sx={{ 
        display: 'flex', 
        alignItems: 'center', 
        justifyContent: 'space-between',
        borderBottom: '1px solid rgba(255,255,255,0.1)',
        pb: 2
      }}>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Avatar 
            sx={{ 
              width: 40, 
              height: 40,
              background: 'linear-gradient(135deg, rgb(59, 130, 246) 0%, rgb(37, 99, 235) 100%)'
            }}
          >
            {clinica?.nome?.charAt(0)}
          </Avatar>
          <Box>
            <Typography variant="h6" sx={{ color: 'white' }}>
              {clinica?.nome}
            </Typography>
            <Typography variant="body2" sx={{ color: '#94A3B8' }}>
              {clinica?.setor || clinica?.especialidade}
            </Typography>
          </Box>
        </Box>
        <IconButton onClick={() => { setEnviado(false); onClose(); }} sx={{ color: 'rgba(255,255,255,0.5)' }}>
          <CloseIcon />
        </IconButton>
      </DialogTitle>

      <DialogContent sx={{ p: 3 }}>
        {enviado ? (
          <Box sx={{ p: 2, textAlign: 'center' }}>
            <Typography variant="h6" sx={{ color: '#3B82F6', mb: 2 }}>
              Mensagem enviada com sucesso!
            </Typography>
            <Typography variant="body2" sx={{ color: '#94A3B8' }}>
              A clínica irá responder em breve.
            </Typography>
          </Box>
        ) : (
          <Box sx={{ p: 2, textAlign: 'center' }}>
            <Typography variant="body2" sx={{ color: '#94A3B8' }}>
              Enviando mensagem para a clínica...
            </Typography>
          </Box>
        )}
      </DialogContent>
    </Dialog>
  );
}
